    private void btnGirisActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGirisActionPerformed
        String username = etUsername.getText();
        String sifre = etSifre.getText();
        Baglanti bg = new Baglanti();
        boolean isThere = bg.Login(username, sifre);
        if(isThere){
            if(username.equals("admin") && sifre.equals("admin")){
                System.out.println("Bu admindir");
                LoginGosterim lg = new LoginGosterim(username);
                lg.setVisible(true);
                lg.btnYonetim.setVisible(true);
                
            }else{
                new LoginGosterim(username).setVisible(true);
            }
            this.setVisible(false);
            
        }else{
            System.out.println("Kayit yok");
        }
    }//GEN-LAST:event_btnGirisActionPerformed

